import org.junit.jupiter.api.Assertions;import org.junit.jupiter.params.ParameterizedTest;import org.junit.jupiter.params.provider.CsvFileSource;import org.openqa.selenium.By;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.io.IOException;import java.util.concurrent.TimeUnit;public class PragmaticPresenceInCountry extends BaseTest {    Logger logger = LoggerFactory.getLogger(PragmaticPresenceInCountry.class);    private By allProviders = (By.xpath("//*[text()[contains(.,'All providers')]]"));    private By pragmaticProvider = By.xpath("//*[text()[contains(.,'Pragmatic')]]");    private By actualProviderScreen = By.xpath("//*[@id='applicationContainer']/div/section[6]/div");    private String baseUrl = "https://www.vipclub.casino/en";    private final static int TIME_OUT = 10;    @ParameterizedTest    @CsvFileSource(resources = "/countryCoordinate.csv", numLinesToSkip = 1)    public void isThereProviderInTheCountryPresence(double latitude, double longitude, String country)            throws IOException, InterruptedException {        utils.setEmulationCoordinates(driver, latitude, longitude);        utils.openMainPage(driver, baseUrl, TIME_OUT);        utils.screenScroll(driver);        utils.waitForElementVisibility(driver, TIME_OUT, allProviders);        logger.info("Main page open and all providers visible");        utils.setProvider(actions, driver, allProviders, pragmaticProvider);        utils.getScreenshot(actualProviderScreen, country, driver);        Assertions.assertEquals("PRAGMATICPLAY", driver.findElement(pragmaticProvider).getText(),                "PragmaticPlay present in " + country);        logger.info("PragmaticPlay present in {} ", country);    }}